import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { Dashboard } from './views/Dashboard';
import { Header } from './views/Header';
import axios, { AxiosResponse } from 'axios';
import { useState, useEffect } from 'react';
import React from 'react';
import { getCompanies, getCurrentUser } from './resources';
import { CompanyForm } from './views/CompanyForm';
import { stylesheet } from 'typestyle';

const styles = stylesheet({
    page: {
        padding: '0 3em',
    },
});
function App() {
    const [companies, setCompanies] = useState<{ name: string; city: string; speciality: string }[]>();
    useEffect(() => {
        getCompanies().then(
            (
                r: AxiosResponse<{
                    companies: { name: string; city: string; speciality: string }[];
                }>
            ) => {
                setCompanies(r.data.companies);
            }
        );
    }, []);

    return (
        <>
            <Router>
                <div className={styles.page}>
                    <Header />
                    <Routes>
                        <Route path="/" element={<Dashboard companies={companies} />} />
                        <Route path="/addCompany" element={<CompanyForm />} />
                    </Routes>
                </div>
            </Router>
            <ToastContainer />
        </>
    );
}

export default App;
