import React, { FormEvent, useState } from 'react';
import { toast } from 'react-toastify';
import { createCompany, register } from '../resources';
import { AxiosErrorWithMessage } from './common';

export function CompanyForm() {
    const [{name, city, specialty}, setFormData] = useState({
        name: '',
        city: '',
        specialty: '',
    });


    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };
    const onSubmit = async (e: FormEvent) => {
        e.preventDefault();

        if (password !== password2) {
            toast.error('Passwords do not match');
        } else {
            const companyData = {
                name,
                city,
                specialty,
            };
            createCompany({ companyData })
                .then((response) => {
                    // localStorage.setItem('user', JSON.stringify(response.data));
                    // setUser(response.data);
                    // setShowRegister();
                    toast.success(' ' + response.data.name, {
                        position: 'top-center',
                        hideProgressBar: true,
                    });
                })
                .catch((e) => {
                    const error = e as AxiosErrorWithMessage;
                    toast.error(error.response.data.message, {
                        position: 'top-right',
                        autoClose: 1500,
                        closeOnClick: true,
                        hideProgressBar: true,
                    });
                });
        }
    };

    return (
        <form onSubmit={onSubmit}>
            <div>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={name}
                    placeholder="Enter company name"
                    onChange={onChange}
                />
            </div>
            <div>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={email}
                    placeholder="Enter your email"
                    onChange={onChange}
                />
            </div>
            <div>
                <input
                    type="password"
                    id="password"
                    name="password"
                    value={password}
                    placeholder="Enter password"
                    onChange={onChange}
                />
            </div>
            <div>
                <input
                    type="password"
                    id="password2"
                    name="password2"
                    value={password2}
                    placeholder="Confirm password"
                    onChange={onChange}
                />
            </div>
            <div>
                <button type="submit">Submit</button>
            </div>
        </form>
    );
}
