import { useState, FormEvent, useEffect } from 'react';
import { toast } from 'react-toastify';
import React from 'react';
import { login } from '../resources';

type AxiosErrorWithMessage = {
    response: {
        data: {
            message: string;
        };
    };
};
type LoginProps = {
    setUser: (user: string | undefined) => void;
};
export function Login({ setUser }: LoginProps) {
    const [{ email, password }, setFormData] = useState({
        email: '',
        password: '',
    });

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };

    const onSubmit = async (e: FormEvent) => {
        e.preventDefault();
        try {
            const data = await login({ loginData: { email, password } });
            console.log(data);
        } catch (e) {
            const error = e as AxiosErrorWithMessage;
            toast.error(error.response.data.message, {
                position: 'top-right',
                autoClose: 1500,
                hideProgressBar: true,
                closeOnClick: true,
            });
        }
    };

    return (
        <>
            <section className="heading">
                <h1>Login</h1>
                <p>Login and start setting goals</p>
            </section>

            <section className="form">
                <form onSubmit={onSubmit}>
                    <div className="form-group">
                        <input
                            type="email"
                            // className="form-control"
                            id="email"
                            name="email"
                            value={email}
                            placeholder="Enter your email"
                            onChange={onChange}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            // className="form-control"
                            id="password"
                            name="password"
                            value={password}
                            placeholder="Enter password"
                            onChange={onChange}
                        />
                    </div>

                    <div className="form-group">
                        <button type="submit" className="btn btn-block">
                            Submit
                        </button>
                    </div>
                </form>
            </section>
        </>
    );
}
