import React, { useState } from 'react';
import { classes, stylesheet } from 'typestyle';
import { Dropdown } from '../utils/Dropdown';

const styles = stylesheet({
    dashboardPage: {
        display: 'flex',
        // justifyContent: 'center',
        // flexDirection: 'column',
        height: '85vh',
    },
    dashboardList: {
        display: 'flex',
        flexDirection: 'column',
        $nest: {
            '& > *': {
                padding: '0.5rem',
            },
        },
    },
    company: {},
    gridLayout: {
        display: 'grid',
        gridTemplateColumns: 'repeat(4, 1fr) ',
        // gridTemplateRows: '1fr 1fr',
    },
    filters: {
        // marginTop: 'auto',
        display: 'flex',
        marginLeft: 'auto',
        $nest: {
            '& > *:first-child': {
                marginRight: '1rem',
            },
        },
    },
    input: {
        display: 'block',
        height: '1rem',
        minWidth: '4rem',
        backgroundColor: '#A8CCC9',
        border: `1px solid gray`,
        borderRadius: 4,
        padding: '0.5rem',
        color: 'black',
        $nest: {
            '&::placeholder': {
                color: 'gray',
            },
            '&:hover': {
                backgroundColor: 'brown',
            },
            '&:focus': {
                borderWidth: 2,
                borderColor: 'blue',
                outline: `none`,
            },
        },
    },
});

type DashboardProps = {
    companies: { name: string; city: string; speciality: string }[] | undefined;
};

export function Dashboard({ companies }: DashboardProps) {
    const specialities = ['Demolish', 'Plumbing', 'Excavation'];
    //todo add a spinner
    const [searchInput, setSearchInput] = useState('');
    const [selectedFilter, setSelectedFilter] = useState('');
    if (!companies) return <div>Nothing here</div>;

    if (companies.length === 0) return <div>No companies</div>;

    console.log(selectedFilter);

    return (
        <div className={styles.dashboardPage}>
            <div className={styles.dashboardList}>
                <header className={styles.gridLayout}>
                    <span>logo</span>
                    <span>Name</span>
                    <span>city</span>
                    <span>speciality</span>
                </header>
                {companies
                    .filter((company) =>
                        company.name
                            .toLocaleLowerCase()
                            .includes(
                                searchInput.toLocaleLowerCase() || selectedFilter.toLocaleLowerCase()
                            )
                    )
                    .map((company) => {
                        return (
                            <div key={company.name} className={styles.gridLayout}>
                                <span>image</span>
                                <span>{company.name}</span>
                                <span>{company.city}</span>
                                <span>{company.speciality}</span>
                            </div>
                        );
                    })}
            </div>
            <div className={styles.filters}>
                <input
                    className={styles.input}
                    type={'text'}
                    placeholder={'Filter companies by name'}
                    value={searchInput}
                    onChange={(e) => setSearchInput(e.target.value)}
                />
                <Dropdown
                    items={specialities}
                    selectedFilter={selectedFilter}
                    setSelectedFilter={setSelectedFilter}
                    title={'Select filter'}
                />
            </div>
        </div>
    );
}
