                    {/*<Link to="/login">Login</Link>*/}
                    <button onClick={() => setShowLogin(true)}>Login</button>
                    {/*<button>Login</button>*/}
                    <button>Register</button>
                    <button onClick={() => localStorage.removeItem('user')}>Logout</button>
                </div>
            ) : (
                user.name
