import React, { ReactElement, useState } from 'react';
import { classes, stylesheet } from 'typestyle';

const styles = stylesheet({
    hideConfirmation: {
        display: 'none',
    },
    tick: {
        margin: '0 10px 0 20px',
        fontSize: '25px',
    },
    containerStyles: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-start',
        position: 'absolute',
        top: '11%',
        right: '20px',
        width: '250px',
        height: '50px',
        borderRadius: '5px',
        textAlign: 'center',
        fontSize: '18px',
        zIndex: 1000,
        color: '#f2f2f2',
    },
    showSuccess: {
        background: 'green',
    },
    showError: {
        background: 'red',
    },
});

interface ConfirmationToastProps {
    text?: string;
    error?: boolean;
}

const defaultText = 'Updated Successfully';

export default function Toast({ text = defaultText, error }: ConfirmationToastProps): ReactElement {
    const [show, setShow] = useState(true);
    return (
        <>
            {show ?
                <div
                    className={classes(
                        styles.containerStyles,
                        error ? styles.showError : styles.showSuccess
                    )}
                    onAnimationEnd={setTimeout(() => {
                        's';
                    }, 3000)}
                >
                    <i className={'fa fa-check'}></i>

                    <div>{text}</div>
                </div> : null
            }
        </>
    );
}
