import { useState, FormEvent } from 'react';
import { toast } from 'react-toastify';
import React from 'react';
import { login } from '../resources';
import { AxiosErrorWithMessage } from './common';
import { useNavigate } from 'react-router-dom';

type LoginProps = {
    setUser: (user: { name: string } | null) => void;
    setShowLogin: () => void;
};
export function Login({ setUser, setShowLogin }: LoginProps) {
    const [{ email, password }, setFormData] = useState({
        email: '',
        password: '',
    });

    const navigator = useNavigate();

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }));
    };

    const onSubmit = async (e: FormEvent) => {
        e.preventDefault();
        try {
            const response = await login({ loginData: { email, password } });
            localStorage.setItem('user', JSON.stringify(response.data));
            setUser(response.data);
            setShowLogin();
            toast('Welcome ' + response.data.name, {
                position: 'top-center',
                autoClose: 1500,
                closeOnClick: true,
                hideProgressBar: true,
            });
        } catch (e) {
            const error = e as AxiosErrorWithMessage;
            toast.error(error.response.data.message, {
                position: 'top-right',
                autoClose: 1500,
                closeOnClick: true,
                hideProgressBar: true,
            });
        }
    };

    return (
        <>
            <section className="form">
                <form onSubmit={onSubmit}>
                    <div className="form-group">
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={email}
                            placeholder="Enter your email"
                            onChange={onChange}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={password}
                            placeholder="Enter password"
                            onChange={onChange}
                        />
                    </div>

                    <div>
                        <button type="submit">Submit</button>
                    </div> 
                </form>
            </section>
        </>
    );
}
